import { isServer } from './isServer.mjs';

/** @tossdocs-ignore */
function getOSByUserAgent() {
  if (isServer()) {
    return false;
  }

  var isIos = window.navigator.userAgent.match(/ipad|iphone/i) !== null;

  if (isIos) {
    return 'ios';
  }

  var isAndroid = window.navigator.userAgent.match(/Android/i) !== null;

  if (isAndroid) {
    return 'android';
  }

  return 'web';
}

export { getOSByUserAgent };
