import { isServer } from './isServer.mjs';

/** @tossdocs-ignore */
function isMobileWeb() {
  if (isServer()) {
    return false;
  }

  if (navigator.userAgent.match(/ipad|iphone/i) !== null || navigator.userAgent.match(/Android/i) !== null) {
    return true;
  }

  return false;
}

export { isMobileWeb };
